INSERT INTO `wp_options` VALUES (794,'learndash-hub-projects-api','a:2:{s:8:\"projects\";a:24:{s:8:\"sfwd-lms\";a:19:{s:4:\"name\";s:13:\"LearnDash LMS\";s:10:\"project_id\";i:57;s:14:\"latest_version\";s:7:\"4.4.1.2\";s:12:\"download_url\";s:81:\"https://checkout.learndash.com/wp-json/learndash/v1/repo/plugin/sfwd-lms/download\";s:8:\"requires\";s:0:\"\";s:6:\"tested\";s:0:\"\";s:12:\"requires_php\";s:0:\"\";s:12:\"last_updated\";s:19:\"2022-12-14 18:03:50\";s:8:\"sections\";a:3:{s:11:\"description\";s:422:\"Turn your WordPress site into a learning management system.\r\n\r\nEasily create &amp; sell courses, deliver quizzes, award certificates, manage users, download reports, and so much more! By using LearnDash you have access to the latest e-learning industry trends for creating robust learning experiences.\r\n\r\nSee the <a href=\"https://www.learndash.com/wordpress-course-plugin-features/\">Features</a> page for more information.\";s:12:\"installation\";s:424:\"If the auto-update is not working, you always have the option to update manually. Please note, a full backup of your site is always recommended prior to updating.\r\n\r\nDeactivate and delete your current version of LearnDash LMS.\r\nDownload the latest version of LearnDash from our support site.\r\nUpload the zipped file via PLUGINS &gt; ADD NEW, or to wp-content/plugins.\r\nActivate the LearnDash LMS plugin via the PLUGINS menu.\";s:9:\"changelog\";s:11381:\"<strong>4.4.1.2</strong><br/><ul>\n<li>Added filter to quiz results output</li>\n<li>Fixed passed shared quiz progress between different courses</li>\n</ul>\n<br/><strong>4.4.1.1</strong><br/><ul>\n<li>Fixed design wizard for Kadence starter templates installer</li>\n</ul>\n<br/><strong>4.4.1</strong><br/><ul>\n<li>Added LearnDash section to Site Health report</li>\n<li>Updated Setup Wizard to create profile page</li>\n<li>Fixed export only exporting 5 quizzes</li>\n<li>Fixed expand button on LearnDash User Groups output</li>\n</ul>\n<br/><strong>4.4.0.1</strong><br/><ul>\n<li>Added Setup Wizard</li>\n<li>Added in app support documentation</li>\n</ul>\n<br/><strong>4.4.0</strong><br/><ul>\n<li>Added Reset Password page to Setup Wizard</li>\n<li>Added index.php to LearnDash cache uploads directory on server to prevent file browsing</li>\n<li>Added Reset Password block/shortcode</li>\n<li>Added login form functionality to LearnDash Registration block</li>\n<li>Added filter in Purchase Invoice PDF to control displayed fields</li>\n<li>Updated currency code selector inputs in Setup Wizard and Payments settings page</li>\n<li>Updated failed import messaging when using LimitRequestBody setting in .htaccess</li>\n<li>Updated [ld<em>course</em>resume] shortcode to redirect to correct step in certain conditions</li>\n<li>Updated Order Overview page to display a message if the user already has access</li>\n<li>Fixed ensuring that media files exist when importing an export</li>\n<li>Fixed translation languages not being installable/downloadable when LD Licensing &amp; Management plugin is active</li>\n<li>Fixed correct quiz score calculation when using single and multiple choice questions</li>\n<li>Fixed price formatting issues when using coupons on initial coupon activation</li>\n<li>Fixed issue with the LearnDash Quiz Complete block output while specifying a user ID</li>\n<li>Fixed password strength message on registration form</li>\n<li>Fixed Fill In The Blanks quiz questions when using &lt; or &gt; as answers</li>\n<li>Fixed quiz saving interval value not saving</li>\n<li>Fixed [visitor] and [student] shortcodes outputting content without a course ID set</li>\n<li>Fixed Enable Nested URLs option not enabling when enabling Shared Course Steps</li>\n<li>Fixed Course Selector dropdown display on Topics listing page</li>\n<li>Fixed issue with video progression logic on lessons and topics</li>\n<li>Fixed [ld_certificate] shortcode display when quiz is taken multiple times</li>\n<li>Fixed setting course to Buy Now/Subscribe using REST API</li>\n</ul>\n<br/><strong>4.3.1.3</strong><br/><ul>\n<li>Updated LMS License page messaging for admin users that don&#039;t have access to view licensing details</li>\n<li>Fixed issue with licensing system upgrade notice message displaying when license plugin was inactive</li>\n</ul>\n<br/><strong>4.3.1.2</strong><br/><ul>\n<li>Fixed issue where empty user access list prevented access to licensing verification and addons</li>\n</ul>\n<br/><strong>4.3.1.1</strong><br/><ul>\n<li>Updated License Visibility feature to LearnDash LMS &gt; Settings &gt; Advanced &gt; License Visibility</li>\n<li>Fixed showing update notice when using latest version of plugin</li>\n</ul>\n<br/><strong>4.3.1</strong><br/><ul>\n<li>Updated removing top level LearnDash Hub menu item</li>\n<li>Updated LearnDash Hub menu to LearnDash LMS -&gt; Settings -&gt; LMS License</li>\n<li>Fixed issue with imported export not properly associating user progress to courses</li>\n</ul>\n<br/><strong>4.3.0.2</strong><br/><ul>\n<li>Added support for LearnDash Hub</li>\n</ul>\n<br/><strong>4.3.0.1</strong><br/><ul>\n<li>Fixed [student] shortcode not accepting user ID parameter</li>\n<li>Fixed protecting file downloads in LearnDash upload directory</li>\n<li>Fixed importing exported files error on Windows</li>\n</ul>\n<br/><strong>4.3.0</strong><br/><ul>\n<li>Added import/export feature</li>\n<li>Fixed date handling logic</li>\n<li>Fixed exported data showing incorrect points</li>\n<li>Fixed changelog data being stored in cache</li>\n<li>Fixed question settings display logic</li>\n<li>Fixed showing comments on open course in focus mode</li>\n<li>Fixed author filter in submitted essays and assignments</li>\n<li>Fixed data sanitization in quizzes</li>\n<li>Fixed centering Take this Course button on mobile</li>\n<li>Fixed course progression between topics in different lessons</li>\n<li>Fixed course progression between sample lesson and topic</li>\n<li>Fixed Expand All/Collapse functionality in Group overview page</li>\n</ul>\n<br/><strong>4.2.1.2</strong><br/><ul>\n<li>Fixed issue progressing from quiz to next step in course</li>\n</ul>\n<br/><strong>4.2.1</strong><br/><p>Fixed correct content encoding on report download\nFixed video shown setting not saving in REST API\nFixed single comma in course/group price field not being interpreted correctly\nFixed quiz associated topic being accessible when parent lesson uses video progression\nFixed course progression when using a sample lesson\nFixed course resume block not determining correct post type\nFixed search not displaying in [ld<em>profile] shortcode\nFixed incorrect step redirect when using video progression on a lesson\nFixed sending purchase success email for RazorPay payment processor\nFixed sending purchase invoice emails for PayPal and Stripe addon\nFixed creating course when setting materials in REST API\nFixed lesson not associating to course when using Course Wizard\nFixed slow query performance in REST API\nFixed quiz summary not allowing questions to be reviewed\nFixed learndash</em>quiz_completed hook not firing on a failed quiz\nFixed course steps rearranging when shared steps is toggled on/off</p>\n<br/><strong>4.2.0.1</strong><br/>\n<br/><strong>4.2.0</strong><br/><ul>\n<li>Added course cloning allowing you to easily duplicate a course and steps within that course</li>\n<li>Added bulk editing allowing you to change settings values for a course/group based on certain parameters</li>\n<li>Added Razorpay as an additional payment provider</li>\n<li>Added purchase invoice email functionality that allows your users to receive a purchase invoice upon buying a course/group</li>\n<li>Added drip content support for all step types, now topics and quizzes can also be dripped</li>\n<li>Updated sample indicator on lessons to not display for already enrolled users</li>\n<li>Updated messaging to show alert to complete previous step when on a sample lesson for a logged in user</li>\n<li>Updated personal data export to include quiz attempts</li>\n<li>Updated Spotlightr video to support autoplay, pause on onfocus and resume settings</li>\n<li>Updated styling improvements on checkout registration process</li>\n<li>Updated next/previous course navigation buttons to be centered on mobile devices</li>\n<li>Fixed lesson topic listing not respecting per page setting</li>\n<li>Fixed issue creating steps in API</li>\n<li>Fixed issue with a passed quiz not completing a lesson if the &#039;Click here to continue&#039; button is not clicked at end of quiz</li>\n<li>Fixed clicking &#039;Start Quiz&#039; button forcing a scroll to the top of the page</li>\n<li>Fixed not being able to disconnect Stripe Connect if account is deleted in Stripe</li>\n<li>Fixed allowing student to mark a step as complete if any sub steps are not marked as completed</li>\n<li>Fixed ld<em>course</em>info block not displaying properly on front end view versus the editor</li>\n<li>Fixed importing a quiz with 0% passing score set not remaining at 0%</li>\n<li>Fixed tooltip display issue in course content step listing for mobile devices</li>\n<li>Fixed oEmbed videos not rendering correctly in quiz correct/incorrect messages</li>\n<li>Fixed reports not getting correct data when empty values exist</li>\n<li>Fixed being able to mark a course as complete without passing a lesson quiz</li>\n<li>Fixed course purchase success email not being sent when using Stripe Connect</li>\n<li>Fixed quiz save/resume breaking when certain characters are contained in correct/incorrect messages</li>\n<li>Fixed registering LearnDash custom post types before loading REST data</li>\n<li>Fixed flushing rewrite rules on every request if permalink settings not saved</li>\n<li>Fixed clickable calendar icons on drip steps in Focus Mode</li>\n<li>Fixed payment processor filter in transactions listings</li>\n<li>Fixed nested unordered list display</li>\n<li>Fixed content tabs to have unique ID values</li>\n<li>Fixed updating group progress calculation when manually marking a course as complete</li>\n<li>Fixed not linking to comments if Assignments custom post type is not public</li>\n<li>Fixed course<em>content expand all button not expanding content when course</em>content shortcode is displayed elsewhere on the page</li>\n<li>Fixed user not being enrolled to a group when Buy Now price is set to empty or 0</li>\n<li>Fixed output of LEARNDASH<em>TEMPLATE</em>CONTENT_METHOD define</li>\n<li>Fixed showing challenge exam if course prerequisites have not been met</li>\n<li>Fixed accessing Assignments custom post type by URL </li>\n<li>Fixed accessing Essay custom post type by URL</li>\n<li>Fixed passed quiz redirect logic to proceed to next incomplete step</li>\n</ul>\n<br/><strong>4.1.2</strong><br/><ul>\n<li>Added filter and hook to control the redirect at the end of the LearnDash setup wizard</li>\n</ul>\n<br/><strong>4.1.0</strong><br/><ul>\n<li>Added course creation playlist allowing you to create courses from a YouTube, Vimeo, or Wistia playlist</li>\n<li>Added coupon support both flat rate and percentage based discounts</li>\n<li>Added mark incomplete feature as a setting in LearnDash core</li>\n<li>Added additional GDPR export/delete data sets</li>\n<li>Added global default currency support</li>\n<li>Added focus mode option to change sidebar position (left or right) includes RTL support</li>\n<li>Updated assignments to open in the WordPress admin to improve usability </li>\n<li>Fixed carriage returns and json parsing options in quiz submissions</li>\n<li>Fixed Course ID in ajaxQuizLoadData being blank sometimes</li>\n<li>Fixed minor bugs with quiz save &amp; resume functionality</li>\n<li>Fixed PayPal transaction in the report tab showing as draft if the IPN response hasn&#039;t been returned</li>\n<li>Fixed course navigation not showing on open courses</li>\n</ul>\n<br/><strong>4.0.0</strong><br/><ul>\n<li>Added new getting started wizard</li>\n<li>Added learning paths (challenge exams)</li>\n<li>Added Stripe Connect integration</li>\n<li>Added course infobar Gutenberg block </li>\n<li>Added post status selectors to columns for post listing</li>\n<li>Added pagination for quizzes for ld_profile block and shortcode</li>\n<li>Added ability to use payment button shortcode for groups</li>\n<li>Added sample label in course builder for sample lessons</li>\n<li>Updated compatibility for widget preview for blocks with full site editing in WP 5.9 (FSE)</li>\n<li>Updated select2 library logic</li>\n<li>Updated REST API V2 Route v2/user/courses/ to include all course fields and certificates</li>\n<li>Fixed emails not sending as plain text</li>\n<li>Fixed error when switching to legacy theme</li>\n<li>Fixed new user email HTML content being received as an attachment</li>\n<li>Fixed purchasing access to a group sends the course purchase successs email</li>\n<li>Fixed conflict with all in one SEO (AISEO)</li>\n<li>Fixed PHP notices</li>\n<li>Fixed placeholder text on Select2 instances</li>\n</ul>\n\";}s:17:\"short_description\";s:82:\"LearnDash LMS Plugin - Turn your WordPress site into a learning management system.\";s:7:\"banners\";a:2:{s:3:\"low\";s:82:\"https://plugins.learndash.com/wp-content/uploads/2022/03/learndash-core_banner.jpg\";s:4:\"high\";s:82:\"https://plugins.learndash.com/wp-content/uploads/2022/03/learndash-core_banner.jpg\";}s:11:\"screenshots\";a:1:{i:0;s:82:\"https://plugins.learndash.com/wp-content/uploads/2022/03/learndash-core_banner.jpg\";}s:5:\"icons\";a:1:{s:7:\"default\";s:76:\"https://plugins.learndash.com/wp-content/uploads/2022/03/ld-plugin-image.jpg\";}s:6:\"author\";s:9:\"LearnDash\";s:10:\"author_uri\";s:26:\"https://www.learndash.com/\";s:11:\"requires_ld\";s:0:\"\";s:10:\"plugin_uri\";s:0:\"\";s:4:\"tags\";s:0:\"\";s:4:\"slug\";s:8:\"sfwd-lms\";}s:18:\"learndash-propanel\";a:19:{s:4:\"name\";s:21:\"ProPanel by LearnDash\";s:10:\"project_id\";i:61;s:14:\"latest_version\";s:7:\"2.1.4.2\";s:12:\"download_url\";s:91:\"https://checkout.learnd